import Link from 'next/link';
import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch';
import Weather from '../components/Weather';

const Index = (props) => (
  <Layout>
    <h1>nextJS App</h1>
    <Weather weather={props.nyWeather} />
    <p>{props.nyWeather}</p>
  </Layout>
)

Index.getInitialProps = async () => {
    const response = await fetch('https://api.openweathermap.org/data/2.5/forecast?zip=11596,us&APPID=c9a0604ff76350b32823210bc6388bcb');
    const nyWeather = await response.json()
    console.log(nyWeather)
    return {
        nyWeather: nyWeather
    }

}

export default Index;
