import React, { Component } from 'react';
import {
  Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle, Button
} from 'reactstrap';

class Weather extends Component {
    constructor(props){
        super(props)

    }
    render(){
        return(
            <div className="container">

            <Card className="card">
            <CardTitle>{this.props.info.name} Weather</CardTitle>
            { (this.props.currentWeather.weather[0].description === 'overcast clouds')? <CardImg top width="100%" src='/static/cloud.png' alt="Card image cap" />: <p></p>
            }

              <CardBody>

              <CardSubtitle>Country: {this.props.info.country}</CardSubtitle>
              <CardText>
              <p></p>
              <p>Lattitude: {this.props.info.coord.lat}</p>
              <p>Longitude: {this.props.info.coord.lon}</p>
              <p>Temp: {((this.props.currentWeather.main.temp * 9/5) - 459.67).toFixed(2)}</p>
              <p>Feels Like: {((this.props.currentWeather.main.feels_like* 9/5) - 459.67).toFixed(2)}</p>
              <p>Humidity: {this.props.currentWeather.main.humidity} %</p>
              <p>Windspeed: {this.props.currentWeather.wind.speed}</p>
              </CardText>
              <Button>Button</Button>
              </CardBody>
            </Card>
            <style jsx>{`
              h1 {
                  font-family: 'Open Sans';
                  color: red;
              },
              Card {
                background-color: lightblue;
                text-align:center;
                width:50%;
                height:50%;
              }

          `}</style>


            </div>
        )
    }
}

export default Weather;
