import Link from 'next/link';
import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch';
import Weather from '../components/Weather';

const Index = (props) => (

  <div>
    <h1>{props.nyWeather.name} Weather</h1>

    <p>Country: {props.nyWeather.country}</p>
    <p>Lattitude: {props.nyWeather.coord.lat}</p>
    <p>Longitude: {props.nyWeather.coord.lon}</p>
    <p>Temp: {props.currentWeather.main.temp}</p>
  </div>

)

Index.getInitialProps = async () => {
    const response = await fetch('https://api.openweathermap.org/data/2.5/forecast?zip=11596,us&APPID=c9a0604ff76350b32823210bc6388bcb');
    const weather = await response.json()
    const nyWeather = await weather.city
    const currentWeather = await weather.list[0]
    console.log(nyWeather)
    console.log(currentWeather)
    return {
        nyWeather: nyWeather,
        currentWeather: currentWeather
    }

}

export default Index;
