import Link from 'next/link';
import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch';
import Weather from '../components/Weather';

const Index = (props) => (

  <div>
    <h1>{props.nyWeather.name} Weather</h1>

    <p>{props.nyWeather.country}</p>
    <p>Sunrise: {props.nyWeather.sunrise.toTime()}</p>
  </div>

)

Index.getInitialProps = async () => {
    const response = await fetch('https://api.openweathermap.org/data/2.5/forecast?zip=11596,us&APPID=c9a0604ff76350b32823210bc6388bcb');
    const weather = await response.json()
    const nyWeather = await weather.city
    console.log(nyWeather)
    return {
        nyWeather: nyWeather
    }

}

export default Index;
