import React, { Component } from 'react';
import {
  Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle, Button
} from 'reactstrap';

class Weather extends Component {
    constructor(props){
        super(props)

    }
    render(){
        return(
            <div>
            <h1>HEY this is the weather page</h1>
            <Card className="card">
            { (this.props.currentWeather.weather[0].description === 'overcast clouds')? <CardImg top width="10%" src='/static/cloud.png' alt="Card image cap" />: <p></p>
            }

              <CardBody>
              <CardTitle>Card title</CardTitle>
              <CardSubtitle>Card subtitle</CardSubtitle>
              <CardText>Some quick example text to build on the card title and make up the bulk of the card's content.</CardText>
              <Button>Button</Button>
              </CardBody>
            </Card>
            <h1>{this.props.info.name} Weather</h1>

            <p>Country: {this.props.info.country}</p>
            <p>Lattitude: {this.props.info.coord.lat}</p>
            <p>Longitude: {this.props.info.coord.lon}</p>
            <p>Temp: {((this.props.currentWeather.main.temp * 9/5) - 459.67).toFixed(2)}</p>
            <p>Feels Like: {((this.props.currentWeather.main.feels_like* 9/5) - 459.67).toFixed(2)}</p>
            <p>Humidity: {this.props.currentWeather.main.humidity} %</p>
            <p>Description: {this.props.currentWeather.weather[0].description}</p>
            { (this.props.currentWeather.weather[0].description === 'overcast clouds')? <img src='/static/cloud.png'/> : <p></p>
            }
            <p>Windspeed: {this.props.currentWeather.wind.speed}</p>
            <style jsx>{`
               {
                  background-color:red;
              }
          `}</style>
            </div>
        )
    }
}

export default Weather;
